@push('javascript')
<script>
    const modal = document.querySelector('.main-modal');
    const closeButton = document.querySelectorAll('.modal-close');
    var expert = @json($listExpert);    

    const modalClose = () => {
        modal.classList.remove('fadeIn');
        modal.classList.add('fadeOut');
        setTimeout(() => {
            modal.style.display = 'none';
        }, 500);
    }

    const openModal = (id) => {
        modal.classList.remove('fadeOut');
        modal.classList.add('fadeIn');
        modal.style.display = 'flex';

        let detail = null;
        if( expert.length > 0 ){
            detail = expert.find(item => item.id == id)
        }

        if( detail ){
            document.querySelector(".dd-image").src = detail.image;
            document.querySelector(".dd-title").innerHTML  = detail.title;

            let chucVu = detail.fields.find(f => f.meta_key === "config_colums_input_chuyengia_chucvu");
            if (chucVu) {
                document.querySelector(".dd-chucvu").innerHTML  = chucVu.meta_value;
            }

            // Lấy field năm sinh
            let namSinh = detail.fields.find(f => f.meta_key === "config_colums_input_chuyengia_namsinh");
            if (namSinh) {
                document.querySelector(".dd-namsinh").innerHTML  = namSinh.meta_value;
            }

            // Lấy field năm sinh
            let desc = detail.fields.find(f => f.meta_key === "config_colums_editor_chuyengia_trinhdo");            
            if (desc) {
                document.querySelector(".dd-desc").innerHTML  = desc.meta_value;
            }

            let content = detail.fields.find(f => f.meta_key === "config_colums_editor_chuyengia_namgiu");
            if (content) {
                document.querySelector(".dd-content").innerHTML  = content.meta_value;
            }

        }
        
        
    }

    for (let i = 0; i < closeButton.length; i++) {

        const elements = closeButton[i];

        elements.onclick = (e) => modalClose();

        modal.style.display = 'none';

        window.onclick = function (event) {
            if (event.target == modal) modalClose();
        }
    }
  </script>
  <script>
    var swiperB = new Swiper(".swiper-block.swiper-container", {
        centeredSlides: true,
        loop: true,
        speed: 1000, // tốc độ chuyển slide (ms)
        spaceBetween: 20,
        pagination: {
            el: ".swiper-block-2.swiper-pagination",
            clickable: true
        },
        loop: true,
        autoplay: {
            delay:2000,
            disableOnInteraction: false,
        },
        breakpoints: {
            320: {
                slidesPerView: 2
            },
            768: {
                slidesPerView: 2
            },
            1024: {
                slidesPerView: 3
            },
            1366: {
                slidesPerView: 4
            },
        }
    });
  </script>
